mapper.select:
select
`id`,`file_ext`,`file_md5`,`file_size`,`original_full_name`,`original_file_name`,`persisted_file_name`,`parent_id`,`path`,`is_directory`,`create_id`,`create_name`,`create_date`,`update_date`,`update_name`,`update_id`
from sys_attachment

    and id = #{id}
    and file_ext = #{fileExt}
    and file_md5 = #{fileMd5}
    and file_size = #{fileSize}
    and original_full_name =
        #{originalFullName}

    and original_file_name =
        #{originalFileName}

    and persisted_file_name =
        #{persistedFileName}

    and parent_id = #{parentId}
    and path = #{path}
    and is_directory = #{isDirectory}
    and create_id = #{createId}
    and create_name = #{createName}
    and create_date = #{createDate}
    and update_date = #{updateDate}
    and update_name = #{updateName}
    and update_id = #{updateId}



        order by ${sort} ${order}


        order by id desc



    limit #{offset}, #{limit}

select count(*) from sys_attachment

    and id = #{id}
    and file_ext = #{fileExt}
    and file_md5 = #{fileMd5}
    and file_size = #{fileSize}
    and original_full_name =
        #{originalFullName}

    and original_file_name =
        #{originalFileName}

    and persisted_file_name =
        #{persistedFileName}

    and parent_id = #{parentId}
    and path = #{path}
    and is_directory = #{isDirectory}
    and create_id = #{createId}
    and create_name = #{createName}
    and create_date = #{createDate}
    and update_date = #{updateDate}
    and update_name = #{updateName}
    and update_id = #{updateId}

select *
from (
select permission, sa.*
from sys_attachment_role sar
left join sys_attachment sa on sar.attactment_id = sa.id

    1=1
    and relation_id = #{userId}
    and permission like '%view%'
    and sa.parent_id = #{parentId}

) att
